import React, { useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import LineChart from '../components/Chart';
import axios from 'axios';

const Stocks = () => {   
    const [chartData, setChartData] = React.useState(null);
    const [summary, setSummary] = React.useState(null);
    const[flag, setFlag] = React.useState(false);
    const apiKey = process.env.REACT_API_API_KEY;

    useEffect(() => {
        const fetchTrend = async () => {
            try{
                const options = {
                    method: 'GET',
                    url: `https://yfapi.net/v8/finance/chart/${state}?range=1mo&inteval=1d`,
                    headers: {
                        'x-api-key': apiKey
                    }
                    };
                    const response = await axios.request*(options);
                    setChartData({
                        labels: response.data.chart.result[0].timestamp.map((elem) => new Date (elem*1000).toLocaleDateString("en-US")),
                            datasets: [
                            {
                                label: 'Changes Reflected Daily',
                                data: response.data.chart.result[0].indicators.quote[0].close,
                                backgroundColor: [
                                                'rgba(75,192,192,1)',
                                                 'rgba(54, 162, 235, 0.2)',
                                                 'rgba(255, 206, 86, 0.2)',
                                                 'rgba(75, 192, 192, 0.2)',
                                                 'rgba(153, 102, 255, 0.2)',
                                                 'rgba(255, 159, 64, 0.2)'
                                ],
                                borderWidth: 5
                            }
                        ],
                    });
                    setSummary(response.data.chart.result[0].meta);
                    setFlag(response.data.chart.result);
                } catch (error){
                    console.error(error);
                }
            }
            fetchTrend();
        }, [apiKey, state]);

    return (
        <>{
            !flag ? (
                <div className='container'>
                    <div className='row'>
                        <div className='card rounded shadow-lg m-2 chart-true'>
                            <p className='error-message' style={{padding: "9px"}}>Loading Data...</p>
                        </div>
                    </div>
                </div>                
            ) : (
                <div className='stock-row'></div>
            )
        }

        </>
    )
}

export default Stocks;
